import polars as pl
import plotly.express as px

wide_df = pl.DataFrame(
    {
        "nation": ["South Korea", "China", "Canada"],
        "gold": [24, 10, 9],
        "silver": [13, 15, 12],
        "bronze": [9, 12, 12],
    }
)

fig = px.bar(
    wide_df,
    x="nation",
    y=["gold", "silver", "bronze"],
    title="Wide-Form Input"
)

fig.show()
